mapper.select:
SELECT COUNT(CAM_ID)
FROM PUSH_CAMPAIGNS
WHERE USER_ID = #{userId}

		SELECT CAM_ID, PUSH_TITLE, REQ_DATE, TARGET_CNT, SUCCESS_CNT, OPEN_CNT, SUCCESS_CNT - OPEN_CNT NOOPEN_CNT, 
      		   TARGET_CNT - SUCCESS_CNT FAIL_CNT, CLICK_CNT, SMS_YN 
		FROM PUSH_CAMPAIGNS
		WHERE CAM_ID = #{camId}
		AND USER_ID = #{userId};
