choose.when:
UPDATE PUSH_CAMPAIGNS
SET SUCCESS_CNT = SUCCESS_CNT + 1
WHERE CAM_ID = #{CAM_ID}

UPDATE PUSH_CAMPAIGNS
SET OPEN_CNT = OPEN_CNT + 1
WHERE CAM_ID = #{CAM_ID}

UPDATE PUSH_CAMPAIGNS
SET CLICK_CNT = CLICK_CNT + 1
WHERE CAM_ID = #{CAM_ID}

choose.otherwise:
UPDATE PUSH_CAMPAIGNS
SET TARGET_CNT = TARGET_CNT
WHERE CAM_ID = #{CAM_ID}

mapper.select:
SELECT COUNT(CAM_ID)
FROM PUSH_CAMPAIGNS
WHERE USER_ID = #{userId}

		SELECT CAM_ID, PUSH_TITLE, REQ_DATE, TARGET_CNT, SUCCESS_CNT, OPEN_CNT, SUCCESS_CNT - OPEN_CNT NOOPEN_CNT, 
      		   TARGET_CNT - SUCCESS_CNT FAIL_CNT, CLICK_CNT, SMS_YN 
		FROM PUSH_CAMPAIGNS
		WHERE CAM_ID = #{camId}
		AND USER_ID = #{userId};

SELECT COUNT(c.CAM_ID) 
FROM push_detail d
JOIN push_campaigns c ON c.CAM_ID = d.CAM_ID 
WHERE c.CAM_ID = #{camId}
AND USER_ID = #{userId}
AND RTN_TYPE = 'N';

SELECT COUNT(c.CAM_ID) 
FROM push_detail d
JOIN push_campaigns c ON c.CAM_ID = d.CAM_ID 
WHERE c.CAM_ID = #{camId}
AND USER_ID = #{userId}
AND RTN_TYPE = 'R';

mapper.update:
    UPDATE PUSH_DETAIL
	SET SMS_SEND_YN = 'Y'
	WHERE DETAIL_ID = #{detailId}

    UPDATE sms_detail
	SET RSLT_CODE = #{RSLT_CODE}
	, RSLT_CODE2 = #{RSLT_CODE2}
	, MSG_ID = #{MSG_SEQ}
	WHERE CAM_ID = #{MSG_ETC2}
	AND CUST_ID = #{MSG_ETC3}
