mapper.select:
SELECT USER_ID "userId", USER_NAME "userName", PASSWD "userPasswd", JOIN_DATE "joinDate", PHONE_NUM "phoneNum"
FROM USERS
WHERE user_id = #{user_id} and passwd = #{passwd}

select COUNT(*)
from users
where user_id = #{userId}

    SELECT COUNT(*)
	FROM APP_USERS
	WHERE CUST_ID = #{custId}

mapper.update:
    UPDATE APP_USERS
	SET PHONE_NUM = #{phoneNum}, REQ_DATE = SYSDATE(), DEVICE = #{device}, #{osVer}, APP_VER = #{appVer}
	WHERE CUST_ID = #{custId}
